import React from 'react';
import { Container, Row, Col, Card, ProgressBar } from 'react-bootstrap';

function capitalizeFirstLetter(string) {
  return string.charAt(0).toUpperCase() + string.slice(1);
}


export default function PokemonData(props) {
  return (
    <Container className="mt-2">
      <Row>
        <Col xs={12} md={6}>
          <Card>
            <Card.Header>
              <h5>{capitalizeFirstLetter(props.name)}</h5>
              <img src={props.sprite} alt={props.name} />
            </Card.Header>
            <Card.Body>
              <h5>Abilities</h5>
              {props.abilities.map((ability, key) => (
                <div key={key}>
                  <span>{capitalizeFirstLetter(ability.ability.name)}</span>
                </div>
              ))}
              <h5>Types</h5>
              {props.types.map((type, key) => (
                <div key={key}>
                  <span>{capitalizeFirstLetter(type.type.name)}</span>
                </div>
              ))}
            </Card.Body>
          </Card>
        </Col>
        <Col xs={12} md={6}>
          <Card>
            <Card.Body>
              <h4>Base Stats</h4>
              {props.stats.map((stat, key) => (
                <div key={key}>
                  <strong>{stat.stat.name === 'hp' ? stat.stat.name.toUpperCase() : capitalizeFirstLetter(stat.stat.name)}</strong>
                  <ProgressBar style={{backgroundColor: 'green'}} now={stat.base_stat} max={255} label={stat.base_stat} />
                </div>
              ))}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  )
}
